import React from 'react';

const TutorialPage = () => {
  return (
    <div>
      <h2>How to Use MoodSync'd</h2>
      <p>Follow these simple steps to get a playlist that matches your mood:</p>
      <ol>
        <li><strong>Create an Account or Login:</strong> Start by signing up or logging into your existing account.</li>
        <li><strong>Set Your Preferences:</strong> After logging in, you can set your favorite music genres to personalize your experience.</li>
        <li><strong>Select Your Mood:</strong> From the dashboard, choose a mood that best describes how you feel.</li>
        <li><strong>Get Your Playlist:</strong> Our system will instantly generate a playlist of songs for you.</li>
        <li><strong>Customize and Enjoy:</strong> Like, dislike, or skip songs to fine-tune your playlist.</li>
        <li><strong>Share:</strong> Share your new favorite playlist with friends on social media!</li>
      </ol>
    </div>
  );
};

export default TutorialPage;
